import React,{Component} from 'react';
import {GallerySection,CustomTabList,ImageHolder,GalleryImage} from './gallerypage.style';
import {Row,Col,Container} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Tab, Tabs, TabPanel } from 'react-tabs';
import "react-tabs/style/react-tabs.css";
import { Gallery } from '../../../pagedata/Gallery';

class GalleryPage extends Component{

    openDialogBox(imageArray,idx)
    {
        this.props.openLightBox(imageArray,idx);
    }
    render(){
        return (
            <div>
            {
                <GallerySection id="GalleryContainer">
                    <Container>
                        <Tabs>
                            <CustomTabList>
                            {
                                Gallery.TabList.map((item,idx) => {
                                    return <Tab>{item.Tab}</Tab>
                                })
                            }
                            </CustomTabList>
                            {
                                Gallery.TabContent.map((itemTab,idxTab) => {
                                return <TabPanel>
                                            <Row>
                                            {
                                                itemTab.Tab.Row.map((item,idx) => {
                                                return <Col md={4} onClick={this.openDialogBox.bind(this,itemTab.Tab.Row,idx)}>
                                                    <ImageHolder>
                                                        <GalleryImage 
                                                            src={require("../../../assets/"+item.GalleryImg)} 
                                                            alt=""
                                                        />
                                                    </ImageHolder>
                                                </Col>
                                                })
                                            }
                                            </Row>
                                    </TabPanel>
                                })
                            }
                        </Tabs>
                    </Container>
                </GallerySection>
            }
            </div>
        )
    }
}

export default GalleryPage;
