import styled from 'styled-components';
import {device} from '../../../components/device';
import {Commonpara,SectionHeading} from '../../../components/common.style';

export const WhyChooseUsSection = styled.section`
    display:flex;
    align-items:center;

    @media ${device.laptop} {
        flex-direction:column-reverse;
    }

    @media ${device.minlaptopL} {
        max-width:1140px;
        margin:auto;
    }
`;

export const WhyChooseUsLeftLayout = styled.div`
    width:50%;
    padding-right:50px;
    padding-left:15px;

    @media ${device.laptopL} {
        margin-left: calc(50% - 570px);
    }

    @media ${device.laptopM} {
        margin-left: calc(50% - 480px);
    }

    @media ${device.laptop} {
        width:100%;
        max-width: 720px;
        margin:0px auto;
        padding: 40px 25px;
    }

    @media ${device.tablet} {
        width:100%;
        max-width: 540px;
        margin:0px auto 40px;
        padding: 40px 25px;
    }
`;

export const WhyChooseUsHeading = styled(SectionHeading)`
    text-align:left;
    margin-bottom:40px;

    @media ${device.laptop} {
    }
`;

export const WhyChooseUsSingle = styled.div`

`;

export const Content = styled(Commonpara)`
    text-align:left;
    margin-bottom:${props => props.marginBottom?"20px":"0px"};
`;

export const WhyChooseUsRightLayout = styled.div`
    width:50%;
    flex-shrink:0;


    @media ${device.minlaptopL} {
        padding-right:15px;
    }
    @media ${device.laptop} {
        width:100%;
    }
`;

export const RightImage = styled.img`
    width:100%;
    object-fit:cover;
    @media ${device.laptopM} {
        min-height:560px;
    }
    @media ${device.laptop} {
        height:350px;
    }
    @media ${device.tablet} {
        height:300px;
    }
`;
