import styled from 'styled-components';
import {Commonh4,Commonpara,SectionHeading} from '../../../components/common.style';
import {Row} from 'react-bootstrap';
import {device} from '../../../components/device';

export const KeyServicesSection = styled.section`
    padding:100px 0px;
    background:#f5f5f5;

    @media ${device.tablet} {
        padding-left:10px;
        padding-right:10px;
        padding-top:80px;
        padding-bottom:80px;
    }
`;


export const HeadingLayout = styled.div`
`;

export const OurServicesHeading = styled(SectionHeading)`
    margin-bottom:75px;

    @media ${device.tablet} {
        margin-bottom:60px;
    }
`;

export const KeyServicesCustomContainer = styled.div`
    background:#fff;
    box-shadow: 0px 0px 16px 0px #d0d0d0;
`;


export const KeyServicesRight = styled(Row)`
    align-items:center;
    margin-right:0px !important;

    @media ${device.laptop} {
        margin-right: calc(30px / -2) !important;
    }
`;

export const KeyServicesRightInnerRow = styled(Row)`
    margin-right:0px !important;

    @media ${device.laptop} {
        margin-left: 0px !important;
        padding-bottom: 40px;
    }

    @media ${device.tablet} {
        padding-bottom: 20px;
        padding-top:20px;
    }

    @media ${device.mobileL} {
        padding-bottom: 10px;
        padding-top:10px;
    }
`;

export const KeyServicesCol1 = styled.div`
    text-align:left;
    padding:20px 9px 20px 18px;

    @media ${device.laptop} {
        padding:40px 5px 0px 25px;
    }
    @media ${device.tablet} {
        padding:20px 25px;
    }

    @media ${device.mobileL} {
        padding:20px 15px;
    }
`;

export const KeyServicesCol2 = styled.div`
    text-align:left;
    padding:20px 18px 20px 9px;

    @media ${device.laptop} {
        padding:40px 25px 0px 5px;
    }
    @media ${device.tablet} {
        padding:20px 25px;
    }
    @media ${device.mobileL} {
        padding:20px 15px;
    }
`;
export const IconHeadingLayout = styled.div`
    display:flex;
    align-items:center;
    margin-bottom:20px;
`;

export const KeyServicesIcon = styled.img`
    height: 50px;
    margin-bottom: 0px;
    background: #fff;
    border-radius: 100%;
    box-shadow: 0px 0px 16px 0px #d0d0d0;
`;

export const KeyServicesHeading = styled(Commonh4)`
    color:#3a3a3a;
    font-size: 24px;
    margin-bottom: 0px;
    text-align:left;
    margin-left:15px;

    @media ${device.tablet} {
        font-size: 22px;
    }
`;

export const KeyServicesDesc = styled(Commonpara)`
    color:#7a7f83;
    font-size:14px;
    margin-bottom:0px;
    line-height: 24px;
`;

export const Img = styled.img`
    min-height:500px;
    width:100%;
    object-fit:cover;

    @media ${device.tablet} {
        height:400px;
        min-height:0px;
    }
`;
